exports.submitUrl = async (req, res, next) => {
  console.log(req.user);
  if(req.user.points < 5) return next( new AppError('You donot have sufficent points', 400)); 
  const videoId = await Url.create(req.body);
  let arr = req.user.submittedIds.map(el => el._id);
  arr.push(videoId._id);
  const points = req.body.noOfViews * 5;

  const doc = await User.findByIdAndUpdate(req.user._id, {
    points,
    submittedIds : arr
  },{
    new: true,
    runValidators: false
  });

  // res.send('points updated successfully');


  res.status(201).json({
    status: 'success',
    data: {
      doc
    }
  });
}



exports.updatePoints = async (req, res, next) => {
  const doc = await User.findById(req.user._id);
  const points = doc.pointsIncrease(5);
  doc.save();

  res.send(`${points} points added successfully`);
}

